mapper.select:
SELECT m.id, m.mail_name, m.recipient, m.copy_people, o.operation_name, m.plan_send_time, u.username FROM mail m, operation o, permission p, user u WHERE m.operation_id = o.id AND o.id = p.operation_id AND u.id = p.user_id
AND m.recipient LIKE "%"#{recipient}"%"
AND o.id = #{operationId}
AND m.plan_send_time BETWEEN #{startTime} AND #{endTime}

SELECT m.id, m.mail_name, m.recipient, m.copy_people, o.operation_name, m.send_time, u.username FROM already_send_mail m, operation o, permission p, user u WHERE m.operation_id = o.id AND o.id = p.operation_id AND u.id = p.user_id
AND m.recipient LIKE "%"#{recipient}"%"
AND o.id = #{operationId}
AND m.send_time BETWEEN #{startTime} AND #{endTime}

mapper.delete:
delete from mail
where operation_id = #{operationId,jdbcType=VARCHAR} 
& plan_send_time = #{planSendTime,jdbcType=VARCHAR}
& recipient_address = #{recipientAddress,jdbcType=VARCHAR}
